from unittest.gui import TestCaseGui
class myTests(TestCaseGui):
def testTwo(self): self.assertEqual(add(-2), 0, "Testing that add(-2) returns 0") self.assertEqual(add(6), 8, "Testing that add(6) returns 8") self.assertEqual(add(4), 6, "Testing that add(4) returns 6")
myTests().main()

