from unittest.gui import TestCaseGui
class myTests(TestCaseGui):
def testThree(self): self.assertEqual(change("I'm Bob. "), "I'm Bob. Nice to meet you!", "Tests that change('I'm Bob. '') returns 'I'm Bob. Nice to meet you!'") self.assertEqual(change(""), "Nice to meet you!", "Tests that change() returns 'Nice to meet you!'")
myTests().main()
