def testOne(self): self.assertEqual(reverse("happy"),"yppah","Tested reverse on input of 'happy'") self.assertEqual(reverse("Python"),"nohtyP","Tested reverse on input of 'Python'") self.assertEqual(reverse(""),"","Tested reverse on input of ''")
