import turtle import random
def random_square(tur): """ Post-Condition: square with random small size is drawn at turtle's current location """ side = random.randrange(10, 30) # get random size for _ in range(4): #draw a square in location tur.forward(side) tur.left(90) return tur
def random_location(tur): """ Post-Condition: turtle is moved to a random location on canvas Pre-conditions: Assume canvas is 400 x 400 """ x = random.randrange(-200, 200) # get random x location y = random.randrange(-200, 200) # get random y location tur.penup() tur.goto(x,y) # move to location without drawing tur.pendown() return tur
def random_colour(tur): """ Post-Condition: turtle is a random colour """ tur.color(random.random(), random.random(), random.random()) return tur
def key_r(): """ draw randomly coloured and positioned square """ global alex alex = random_colour(alex) alex = random_location(alex) alex = random_square(alex)
def key_q(): """ close the canvas window """ wn.bye()
def click(x, y): """ go to clicked location, pick random color and draw square """ global alex alex.penup() alex.goto(x, y) alex.pendown() alex = random_colour(alex) alex = random_square(alex)
wn = turtle.Screen() # Set up the window and its attributes alex = turtle.Turtle() # create alex alex.speed(10) # make alex draw fast alex = random_colour(alex) alex = random_location(alex) alex = random_square(alex)
wn.onkey(key_r, 'r') # tell the operating system to execute function 'key_r()' when the 'r' key is pressed on the keyboard wn.onkey(key_q, 'q') # tell the operating system to execute function 'key_q()' when the 'q' key is pressed on the keyboard wn.onclick(click) # tell the operating system to execute function 'click' when the user clicks the mouse wn.listen() # tell the operating system to listen for events on the canvas window

def get_yes_or_no(message):
    valid_input = False
    while not valid_input:
        answer = input(message)
        answer = answer.upper() # convert to upper case
        if answer == 'Y' or answer == 'N':
            valid_input = True
        else:
            print('Please enter Y for yes or N for no.')
    return answer

response = get_yes_or_no('Do you like lima beans? Y)es or N)o: ')
if response == 'Y':
    print('Great! They are very healthy.')
else:
    print('Too bad. If cooked right, they are quite tasty.')
