program.input:
user_text = input("Please enter a word with no spaces:")

if user_text != "None":
   encoded_text = ""
   for c in user_text:
       # convert character to ordinal, add 1, convert back to character
       encoded_text += chr(ord(c)+1)

   print("Encoded word is: ", encoded_text)

else:
   print("User did not enter a word.")

greetings = ["hello", "good bye", "have a nice day", "ciao", "see ya", "bye", "bonjour", "au revoir", "adios"] phrase_greetings = [] word_greetings = []
for greeting in greetings: if ' ' in greeting: #add greeting to phrase greetings phrase_greetings.append(greeting) else: #add to single-word greetings word_greetings.append(greeting)
print("Phrase greetings include:", phrase_greetings) print("Word greetings include:", word_greetings)
