def change_stamp(): """ change the stamp shape of the turtle """ stamp_shape = stamp_list[stamp_index] abi.shape(stamp_shape) print("Abi's shape is now:", stamp_shape)
stamp_list = ["arrow", "turtle", "square", "classic"] stamp_index = 0 wn = turtle.Screen() abi = turtle.Turtle()
input_string = input("How many stamps do you want to create?") input_num = int(input_string)
for _ in range(input_num): change_color() stamp_index = (stamp_index + 1) % len(stamp_list) change_stamp() x = random.randrange(-180, 180) y = random.randrange(-180, 180) abi.penup() abi.goto(x,y) abi.pendown() stampy()
abi.hideturtle()

import turtle import random
def stampy(): """ create a stamp-star, with 6 rotated stamps""" angle = 360/6 for _ in range(6): abi.stamp() abi.right(angle)
def change_color(): """ change the color of the turtle to a random shade """ red = random.random() green = random.random() blue = random.random() abi.color(red, green, blue)
def random_location(): x = random.randrange(-180, 180) y = random.randrange(-180, 180) abi.penup() abi.goto(x,y) abi.pendown()
def random_stamp(): """ change the stamp shape of the turtle """ stamp_list = ["arrow", "turtle", "square", "classic"] stamp_index = random.randrange(4) stamp_shape = stamp_list[stamp_index] abi.shape(stamp_shape)
wn = turtle.Screen() abi = turtle.Turtle()
for _ in range(5): random_location() random_stamp() change_color() stampy()
abi.hideturtle()
